const usersDataStore = require('database/dataStores/users/usersDataStore');
const postsDataStore = require('database/dataStores/posts/postsDataStore');

module.exports = (db) => {
  const obj = Object.assign(
    {},
    db.entities,
    {
      sequelize: db.sequelize,
    },
  );
  return {
    usersDataStore: usersDataStore(obj),
    postsDataStore: postsDataStore(obj),
  };
};
