const getWhereToFetchUser = ({
  id,
  userId,
  email,
  userName,
}) => ({
  ...(id && { id }),
  ...(userId && { userId }),
  ...(email && { email }),
  ...(userName && { userName }),
});

const userRepo = {
  async listUsers({
    attributes = [],
    limit = 50,
    offset = 0,
    orderBy = [
      ['createdAt', 'DESC'],
    ],
  }) {
    try {
      const {
        users: userModel,
      } = this.getDbModels();
      const list = await userModel.findAndCountAll({
        order: orderBy,

        attributes: attributes && Array.isArray(attributes) && attributes.length > 0
          ? attributes
          : { exclude: [] },
        limit,
        offset,
      });
      return {
        data: list && list.rows
          ? list.rows.map(el => el.get({ plain: true }))
          : [],
        pagination: {
          limit,
          offset,
          total: list && list.count
            ? list.count
            : 0,
        },
      };
    } catch (error) {
      throw error;
    }
  },
  async getUser({
    id,
    userId,
    email,
    userName,
    attributes = [],
    lock,
    transaction,
  }) {
    try {
      const {
        users: userModel,
      } = this.getDbModels();
      if (!id && !email) {
        throw new Error('Add id or email to get user.');
      }
      const res = await userModel.findOne({
        where: getWhereToFetchUser({
          id,
          userId,
          email,
          userName,
        }),
        attributes: attributes && Array.isArray(attributes) && attributes.length > 0
          ? attributes
          : { exclude: [] },
        ...(lock != null && { lock }),
        ...(transaction != null && { transaction }),
      });
      if (!res) {
        const msg = email
          ? `Admin doc with email: ${email} not found.`
          : `Admin doc with id: ${id} not found.`;
        throw new Error(msg);
      }
      return res.get({ plain: true });
    } catch (error) {
      throw error;
    }
  },
  async createUser({
    firstName,
    lastName,
    userName,
    email,
    password,
  }) {
    try {
      const {
        users: userModel,
      } = this.getDbModels();
      const res = await userModel.create({
        firstName,
        lastName,
        userName,
        email,
        password,
      });
      return res.get({ plain: true });
    } catch (error) {
      throw error;
    }
  },
};

module.exports = function init({
  users,
  sequelize,
}) {
  return Object.assign(Object.create(userRepo), {
    getDbModels() {
      return {
        users,
      };
    },
    getDB() {
      return sequelize;
    },
  });
};
