const fs = require('fs');
const path = require('path');
const Sequelize = require('sequelize');

module.exports = function init(config) {
  const basename = path.basename(__filename);
  let sequelize;
  const dbOptions = {
    dialect: 'postgres',
    operatorsAliases: false,
    logging: true,
    timezone: '+00:00',
    dialectOptions: {
      ssl: true,
      useUTC: true,
    },
    define: {
      freezeTableName: true,
    },
  };
  if (config.connectionUri) {
    sequelize = new Sequelize(config.connectionUri, dbOptions);
  } else {
    sequelize = new Sequelize(config.database, config.username, config.password, Object.assign(
      {},
      {
        host: config.host,
        port: config.port
      },
      dbOptions,
    ));
  }

  const db = {
    entities: {},
    async authenticate() {
      return sequelize.authenticate();
    },
    async close() {
      return sequelize.connectionManager.close();
    },
    sync: (opt = false) => {
      sequelize.sync({ force: opt });
    },
    async createTransaction() {
      return sequelize.transaction();
    },
    runTransaction: sequelize.transaction.bind(sequelize),
  };

  const modelsPath = `${__dirname}/models`;

  fs
    .readdirSync(modelsPath)
    .filter(file => (file.indexOf('.') !== 0) && (file !== basename) && (file.slice(-3) === '.js'))
    .forEach((file) => {
      const model = sequelize.import(path.join(modelsPath, file));
      db[model.name] = model;
      db.entities[model.name] = model;
    });

  Object.keys(db).forEach((modelName) => {
    if (db[modelName].associate) {
      db[modelName].associate(db);
      db.entities[modelName].associate(db);
    }
  });

  db.sequelize = sequelize;
  db.Sequelize = Sequelize;
  return db;
};
