const adminsInterface = require('./admins/adminsInterface');
const usersInterface = require('./users/usersInterface');

module.exports = (db) => {
  const obj = Object.assign(
    {},
    db.entities,
    {
      sequelize: db.sequelize,
    },
  );
  return {
    adminsInterface: adminsInterface(obj),
    usersInterface: usersInterface(obj),
  };
};
