module.exports = (sequelize, DataTypes) => {
  const Post = sequelize.define('posts', {
    postId: {
      type: DataTypes.UUID,
      defaultValue: DataTypes.UUIDV4,
      allowNull: false,
      unique: true,
    },
    title: {
      type: DataTypes.STRING,
      allowNull: false,
    },
    message: {
      type: DataTypes.STRING,
      allowNull: false,
    },
    userId: {
      type: DataTypes.INTEGER,
      allowNull: false,
      unique: false,
    },
  }, {
    indexes: [
      {
        unique: false,
        fields: [
          'userId',
        ],
      },
      {
        unique: true,
        fields: [
          'postId',
        ],
      },
    ],
    freezeTableName: true,
  });

  return Post;
};
